import React, { useState, useEffect, useMemo } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import CountUp from 'react-countup';
import { 
  XAxis, 
  YAxis, 
  CartesianGrid, 
  Tooltip, 
  ResponsiveContainer,
  Area,
  AreaChart
} from 'recharts';
import { 
  Search, 
  Download, 
  TrendingUp, 
  RefreshCw,
  Users,
  Eye,
  UserCheck,
  ArrowUpRight,
  ArrowDownRight,
  ArrowRight,
  ExternalLink,
  Calendar,
  BarChart3,
  Edit,
  Settings,
  Save,
  X
} from 'lucide-react';

interface Profile {
  id: number;
  username: string;
  profile_name: string;
  followers_count: number;
  following_count: number;
  posts_count: number;
  engagement_rate: number;
  bio: string;
  profile_pic_url: string;
  is_verified: number;
  is_private: number;
  last_updated: string;
}

interface GrowthData {
  date: string;
  followers: number;
  engagement: number;
}

const SplitPanelDashboard: React.FC = () => {
  const [profiles, setProfiles] = useState<Profile[]>([]);
  const [, setLoading] = useState(true);
  const [autoRefresh, setAutoRefresh] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterBy, setFilterBy] = useState('all');
  const [sortBy, setSortBy] = useState('followers');
  const [sortOrder, setSortOrder] = useState<'asc' | 'desc'>('desc');
  const [newProfile, setNewProfile] = useState('');
  const [addingProfile, setAddingProfile] = useState(false);
  const [selectedProfile, setSelectedProfile] = useState<Profile | null>(null);
  const [previousRankings, setPreviousRankings] = useState<Map<string, number>>(new Map());
  const [timeRange, setTimeRange] = useState<'1Y' | '2Y' | '3Y' | '5Y'>('5Y');
  const [editMode, setEditMode] = useState(false);
  const [customGrowthRate, setCustomGrowthRate] = useState(15); // Annual growth rate %
  const [customSeasonalVariation, setCustomSeasonalVariation] = useState(10); // Seasonal variation %

  // Generate impressive growth data for selected profile with customizable parameters
  const generateProfileGrowthData = (profile: Profile | null): GrowthData[] => {
    if (!profile) return [];

    const data: GrowthData[] = [];
    const today = new Date();

    // Determine number of months based on time range
    const monthsMap = { '1Y': 12, '2Y': 24, '3Y': 36, '5Y': 60 };
    const totalMonths = monthsMap[timeRange];

    // Generate monthly data for selected time range
    for (let i = totalMonths - 1; i >= 0; i--) {
      const date = new Date(today);
      date.setMonth(date.getMonth() - i);

      // Simulate realistic long-term growth patterns
      const baseFollowers = profile.followers_count / 1000000; // Convert to millions
      const baseEngagement = profile.engagement_rate;

      // Create impressive growth trends with custom parameters
      const monthsAgo = totalMonths - 1 - i;
      const timeFactor = monthsAgo / (totalMonths - 1); // 0 to 1 over selected period

      // Use custom growth rate
      const annualGrowthRate = customGrowthRate / 100;
      const exponentialGrowth = Math.pow(1 + annualGrowthRate, timeFactor);

      // Use custom seasonal variation
      const seasonalVariation = Math.sin((monthsAgo / 12) * 2 * Math.PI) * (customSeasonalVariation / 100);

      // Add some random noise for realism
      const randomVariation = (Math.random() - 0.5) * 0.03;

      // Calculate current followers (going backwards from current count)
      const currentFollowers = baseFollowers * exponentialGrowth * (1 + seasonalVariation + randomVariation);

      // Engagement tends to decrease as follower count grows (more realistic)
      const engagementDecline = Math.max(0.2, 1 - (timeFactor * 0.3)); // Max 30% decline over time
      const currentEngagement = baseEngagement * engagementDecline * (1 + (Math.random() - 0.5) * 0.08);

      data.push({
        date: date.toISOString().split('T')[0],
        followers: Math.max(0, currentFollowers),
        engagement: Math.max(0.1, currentEngagement)
      });
    }

    return data;
  };

  const selectedProfileGrowthData = useMemo(() => 
    generateProfileGrowthData(selectedProfile), [selectedProfile, timeRange, customGrowthRate, customSeasonalVariation]
  );

  // Fetch profiles
  const fetchProfiles = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/profiles/');
      const data = await response.json();
      setProfiles(data);

      // Store previous rankings for comparison
      const newRankings = new Map<string, number>();
      data.forEach((profile: Profile, index: number) => {
        newRankings.set(profile.username, index + 1);
      });
      setPreviousRankings(newRankings);

      // Auto-select first profile if none selected
      if (!selectedProfile && data.length > 0) {
        setSelectedProfile(data[0]);
      }

      setLoading(false);
    } catch (error) {
      console.error('Error fetching profiles:', error);
      setLoading(false);
    }
  };

  // Add new profile
  const addProfile = async () => {
    if (!newProfile.trim()) return;

    setAddingProfile(true);
    try {
      const response = await fetch('http://localhost:8000/api/scraper/profile', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username: newProfile.trim() }),
      });

      if (response.ok) {
        await fetchProfiles();
        setNewProfile('');
      } else {
        const errorData = await response.json();
        alert(`Error: ${errorData.detail}`);
      }
    } catch (error) {
      console.error('Error adding profile:', error);
      alert('Error adding profile');
    } finally {
      setAddingProfile(false);
    }
  };

  // Load sample profiles
  const loadSampleProfiles = async () => {
    const sampleUsernames = [
      'cristiano', 'leomessi', 'taylorswift', 'ishowspeed', 
      'virat.kohli', 'therock', 'selenagomez', 'kyliejenner'
    ];

    for (const username of sampleUsernames) {
      try {
        await fetch('http://localhost:8000/api/scraper/profile', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ username }),
        });
      } catch (error) {
        console.error(`Error adding ${username}:`, error);
      }
    }

    await fetchProfiles();
  };

  // Export data
  const exportData = () => {
    const csvContent = [
      ['Username', 'Profile Name', 'Followers', 'Following', 'Posts', 'Engagement Rate', 'Last Updated'],
      ...profiles.map(profile => [
        profile.username,
        profile.profile_name || '',
        profile.followers_count.toString(),
        profile.following_count.toString(),
        profile.posts_count.toString(),
        `${profile.engagement_rate.toFixed(2)}%`,
        profile.last_updated
      ])
    ].map(row => row.join(',')).join('\n');

    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `instagram-analytics-${new Date().toISOString().split('T')[0]}.csv`;
    a.click();
    window.URL.revokeObjectURL(url);
  };

  // Get rank change indicator
  const getRankChange = (username: string, currentRank: number) => {
    const previousRank = previousRankings.get(username);
    if (!previousRank) return null;

    const change = previousRank - currentRank;
    if (change > 0) return { direction: 'up', change: Math.abs(change) };
    if (change < 0) return { direction: 'down', change: Math.abs(change) };
    return { direction: 'same', change: 0 };
  };

  // Format numbers
  const formatNumber = (num: number): string => {
    if (num >= 1000000000) return `${(num / 1000000000).toFixed(1)}B`;
    if (num >= 1000000) return `${(num / 1000000).toFixed(1)}M`;
    if (num >= 1000) return `${(num / 1000).toFixed(1)}K`;
    return num.toString();
  };

  // Filter and sort profiles
  const filteredAndSortedProfiles = useMemo(() => {
    let filtered = profiles.filter(profile => {
      const matchesSearch = profile.username.toLowerCase().includes(searchTerm.toLowerCase()) ||
        (profile.profile_name && profile.profile_name.toLowerCase().includes(searchTerm.toLowerCase()));

      if (filterBy === 'verified') return matchesSearch && profile.is_verified;
      if (filterBy === 'private') return matchesSearch && profile.is_private;
      if (filterBy === 'public') return matchesSearch && !profile.is_private;

      return matchesSearch;
    });

    return filtered.sort((a, b) => {
      let aValue = 0, bValue = 0;

      switch (sortBy) {
        case 'followers':
          aValue = a.followers_count;
          bValue = b.followers_count;
          break;
        case 'following':
          aValue = a.following_count;
          bValue = b.following_count;
          break;
        case 'posts':
          aValue = a.posts_count;
          bValue = b.posts_count;
          break;
        case 'engagement':
          aValue = a.engagement_rate;
          bValue = b.engagement_rate;
          break;
      }

      return sortOrder === 'asc' ? aValue - bValue : bValue - aValue;
    });
  }, [profiles, searchTerm, filterBy, sortBy, sortOrder]);

  useEffect(() => {
    fetchProfiles();
  }, []);

  useEffect(() => {
    if (!autoRefresh) return;

    const interval = setInterval(fetchProfiles, 30000); // Refresh every 30 seconds
    return () => clearInterval(interval);
  }, [autoRefresh]);

  return (
    <div className="min-h-screen bg-[#0f1419] text-white font-['Inter']">
      {/* Header */}
      <motion.header 
        className="h-16 bg-[#0f1419] border-b border-[#1a2332] px-6 flex items-center justify-between"
        initial={{ y: -100 }}
        animate={{ y: 0 }}
        transition={{ duration: 0.5 }}
      >
        <div className="flex items-center space-x-4">
          <div className="w-8 h-8 bg-gradient-to-r from-[#00d9ff] to-[#00b8e6] rounded-lg flex items-center justify-center">
            <TrendingUp className="w-5 h-5 text-white" />
          </div>
          <div>
            <h1 className="text-xl font-bold text-white">Instagram Analytics</h1>
            <p className="text-[#8b9bb3] text-sm">Professional Social Media Dashboard</p>
          </div>
        </div>

        <div className="flex items-center space-x-4">
          <motion.button
            onClick={() => setAutoRefresh(!autoRefresh)}
            className={`flex items-center space-x-2 px-4 py-2 rounded-lg transition-all ${
              autoRefresh 
                ? 'bg-[#00d9ff]/20 text-[#00d9ff] border border-[#00d9ff]/30' 
                : 'bg-[#1a2332] text-[#8b9bb3] hover:text-white'
            }`}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            <RefreshCw className={`w-4 h-4 ${autoRefresh ? 'animate-spin' : ''}`} />
            <span className="text-sm font-medium">Auto-refresh</span>
          </motion.button>

          <motion.button
            onClick={exportData}
            className="flex items-center space-x-2 px-4 py-2 bg-[#00d9ff]/20 hover:bg-[#00d9ff]/30 text-[#00d9ff] border border-[#00d9ff]/30 rounded-lg transition-all"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            <Download className="w-4 h-4" />
            <span className="text-sm font-medium">Export</span>
          </motion.button>
        </div>
      </motion.header>

      <div className="flex h-[calc(100vh-64px)]">
        {/* Left Panel - Profile List */}
        <div className="w-1/3 bg-[#1a2332] border-r border-[#243447] flex flex-col">
          {/* Add Profile Section */}
          <motion.div 
            className="p-4 border-b border-[#243447]"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.2 }}
          >
            <h2 className="text-lg font-bold text-white mb-3">Add Profile</h2>
            <div className="flex items-center space-x-2">
              <input
                type="text"
                value={newProfile}
                onChange={(e) => setNewProfile(e.target.value)}
                placeholder="Enter username"
                className="flex-1 px-3 py-2 bg-[#243447] border border-[#1a2332] rounded-lg focus:outline-none focus:border-[#00d9ff] text-white placeholder-[#5a6b82] text-sm"
                onKeyPress={(e) => e.key === 'Enter' && addProfile()}
              />
              <motion.button
                onClick={addProfile}
                disabled={addingProfile}
                className="px-3 py-2 bg-[#00d9ff] hover:bg-[#00b8e6] disabled:bg-[#5a6b82] rounded-lg transition-all text-white text-sm font-medium"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                {addingProfile ? (
                  <RefreshCw className="w-4 h-4 animate-spin" />
                ) : (
                  <ArrowUpRight className="w-4 h-4" />
                )}
              </motion.button>
              <motion.button
                onClick={loadSampleProfiles}
                className="px-3 py-2 bg-[#243447] hover:bg-[#1a2332] border border-[#1a2332] rounded-lg transition-all text-[#8b9bb3] hover:text-white text-sm"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                <Users className="w-4 h-4" />
              </motion.button>
            </div>
          </motion.div>

          {/* Search and Filters */}
          <div className="p-4 border-b border-[#243447] space-y-3">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-[#5a6b82] w-4 h-4" />
              <input
                type="text"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                placeholder="Search profiles..."
                className="w-full pl-10 pr-4 py-2 bg-[#243447] border border-[#1a2332] rounded-lg focus:outline-none focus:border-[#00d9ff] text-white placeholder-[#5a6b82] text-sm"
              />
            </div>

            <div className="flex gap-2">
              <select
                value={filterBy}
                onChange={(e) => setFilterBy(e.target.value)}
                className="flex-1 px-3 py-2 bg-[#243447] border border-[#1a2332] rounded-lg focus:outline-none focus:border-[#00d9ff] text-white text-sm"
              >
                <option value="all">All</option>
                <option value="verified">Verified</option>
                <option value="public">Public</option>
                <option value="private">Private</option>
              </select>

              <select
                value={sortBy}
                onChange={(e) => setSortBy(e.target.value)}
                className="flex-1 px-3 py-2 bg-[#243447] border border-[#1a2332] rounded-lg focus:outline-none focus:border-[#00d9ff] text-white text-sm"
              >
                <option value="followers">Followers</option>
                <option value="following">Following</option>
                <option value="posts">Posts</option>
                <option value="engagement">Engagement</option>
              </select>

              <motion.button
                onClick={() => setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc')}
                className="px-3 py-2 bg-[#243447] hover:bg-[#1a2332] border border-[#1a2332] rounded-lg transition-all text-white"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                {sortOrder === 'desc' ? '↓' : '↑'}
              </motion.button>
            </div>
          </div>

          {/* Profile List */}
          <div className="flex-1 overflow-y-auto">
            <div className="p-4">
              <h3 className="text-sm font-medium text-[#8b9bb3] mb-3">Profiles ({filteredAndSortedProfiles.length})</h3>
              <div className="space-y-2">
                <AnimatePresence>
                  {filteredAndSortedProfiles.map((profile, index) => {
                    const rankChange = getRankChange(profile.username, index + 1);
                    const isSelected = selectedProfile?.id === profile.id;

                    return (
                      <motion.div
                        key={profile.id}
                        className={`p-3 rounded-lg cursor-pointer transition-all ${
                          isSelected 
                            ? 'bg-[#00d9ff]/20 border border-[#00d9ff]/30' 
                            : 'bg-[#243447] hover:bg-[#1a2332] border border-transparent'
                        }`}
                        onClick={() => setSelectedProfile(profile)}
                        whileHover={{ scale: 1.02 }}
                        whileTap={{ scale: 0.98 }}
                        initial={{ opacity: 0, x: -20 }}
                        animate={{ opacity: 1, x: 0 }}
                        transition={{ duration: 0.3, delay: index * 0.05 }}
                      >
                        <div className="flex items-center space-x-3">
                          <div className="relative">
                            <img
                              src={profile.profile_pic_url || `https://ui-avatars.com/api/?name=${profile.username}&background=0ea5e9&color=fff&size=32`}
                              alt={profile.username}
                              className="w-8 h-8 rounded-full border-2 border-[#243447]"
                              onError={(e) => {
                                (e.target as HTMLImageElement).src = `https://ui-avatars.com/api/?name=${profile.username}&background=0ea5e9&color=fff&size=32`;
                              }}
                            />
                            {profile.is_verified && (
                              <div className="absolute -bottom-1 -right-1 w-3 h-3 bg-[#00d9ff] rounded-full flex items-center justify-center">
                                <span className="text-white text-xs">✓</span>
                              </div>
                            )}
                          </div>
                          <div className="flex-1 min-w-0">
                            <div className="flex items-center space-x-2">
                              <span className="text-xs font-bold text-[#5a6b82]">#{index + 1}</span>
                              <span className="text-sm font-medium text-white truncate">@{profile.username}</span>
                              {rankChange && (
                                <motion.span
                                  className={`text-xs ${
                                    rankChange.direction === 'up' ? 'text-[#00ff88]' :
                                    rankChange.direction === 'down' ? 'text-[#ff3b5c]' :
                                    'text-[#8b9bb3]'
                                  }`}
                                  initial={{ scale: 0 }}
                                  animate={{ scale: 1 }}
                                  transition={{ duration: 0.2 }}
                                >
                                  {rankChange.direction === 'up' && '↑'}
                                  {rankChange.direction === 'down' && '↓'}
                                  {rankChange.direction === 'same' && '→'}
                                </motion.span>
                              )}
                            </div>
                            <div className="text-xs text-[#8b9bb3]">
                              {formatNumber(profile.followers_count)} followers
                            </div>
                          </div>
                        </div>
                      </motion.div>
                    );
                  })}
                </AnimatePresence>
              </div>
            </div>
          </div>
        </div>

        {/* Right Panel - Profile Details */}
        <div className="flex-1 bg-[#0f1419] flex flex-col">
          {selectedProfile ? (
            <>
              {/* Profile Header */}
              <motion.div 
                className="p-6 border-b border-[#1a2332]"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: 0.2 }}
              >
                <div className="flex items-center space-x-4">
                  <img
                    src={selectedProfile.profile_pic_url || `https://ui-avatars.com/api/?name=${selectedProfile.username}&background=0ea5e9&color=fff&size=64`}
                    alt={selectedProfile.username}
                    className="w-16 h-16 rounded-full border-2 border-[#243447]"
                    onError={(e) => {
                      (e.target as HTMLImageElement).src = `https://ui-avatars.com/api/?name=${selectedProfile.username}&background=0ea5e9&color=fff&size=64`;
                    }}
                  />
                  <div className="flex-1">
                    <div className="flex items-center space-x-2">
                      <h2 className="text-2xl font-bold text-white">@{selectedProfile.username}</h2>
                      {selectedProfile.is_verified && (
                        <div className="w-6 h-6 bg-[#00d9ff] rounded-full flex items-center justify-center">
                          <span className="text-white text-sm">✓</span>
                        </div>
                      )}
                    </div>
                    {selectedProfile.profile_name && (
                      <p className="text-[#8b9bb3] text-lg">{selectedProfile.profile_name}</p>
                    )}
                    {selectedProfile.bio && (
                      <p className="text-[#8b9bb3] text-sm mt-1">{selectedProfile.bio}</p>
                    )}
                  </div>
                  <motion.a
                    href={`https://instagram.com/${selectedProfile.username}`}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="flex items-center space-x-2 px-4 py-2 bg-[#00d9ff]/20 hover:bg-[#00d9ff]/30 text-[#00d9ff] border border-[#00d9ff]/30 rounded-lg transition-all"
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                  >
                    <ExternalLink className="w-4 h-4" />
                    <span className="text-sm font-medium">View Profile</span>
                  </motion.a>
                </div>
              </motion.div>

              {/* Stats Cards */}
              <motion.div 
                className="p-6"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: 0.4 }}
              >
                <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                  <motion.div 
                    className="bg-[#1a2332] rounded-xl p-4 border border-[#243447]"
                    whileHover={{ y: -2, boxShadow: '0 8px 32px rgba(0, 217, 255, 0.1)' }}
                    transition={{ duration: 0.2 }}
                  >
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="text-[#8b9bb3] text-sm font-medium mb-1">Followers</p>
                        <p className="text-2xl font-bold text-white">
                          <CountUp end={selectedProfile.followers_count} duration={2} separator="," />
                        </p>
                      </div>
                      <Users className="w-8 h-8 text-[#00d9ff]" />
                    </div>
                  </motion.div>

                  <motion.div 
                    className="bg-[#1a2332] rounded-xl p-4 border border-[#243447]"
                    whileHover={{ y: -2, boxShadow: '0 8px 32px rgba(0, 217, 255, 0.1)' }}
                    transition={{ duration: 0.2 }}
                  >
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="text-[#8b9bb3] text-sm font-medium mb-1">Following</p>
                        <p className="text-2xl font-bold text-white">
                          <CountUp end={selectedProfile.following_count} duration={2} separator="," />
                        </p>
                      </div>
                      <UserCheck className="w-8 h-8 text-[#9b7dff]" />
                    </div>
                  </motion.div>

                  <motion.div 
                    className="bg-[#1a2332] rounded-xl p-4 border border-[#243447]"
                    whileHover={{ y: -2, boxShadow: '0 8px 32px rgba(0, 217, 255, 0.1)' }}
                    transition={{ duration: 0.2 }}
                  >
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="text-[#8b9bb3] text-sm font-medium mb-1">Posts</p>
                        <p className="text-2xl font-bold text-white">
                          <CountUp end={selectedProfile.posts_count} duration={2} separator="," />
                        </p>
                      </div>
                      <Eye className="w-8 h-8 text-[#ff3b5c]" />
                    </div>
                  </motion.div>

                  <motion.div 
                    className="bg-[#1a2332] rounded-xl p-4 border border-[#243447]"
                    whileHover={{ y: -2, boxShadow: '0 8px 32px rgba(0, 217, 255, 0.1)' }}
                    transition={{ duration: 0.2 }}
                  >
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="text-[#8b9bb3] text-sm font-medium mb-1">Engagement</p>
                        <p className="text-2xl font-bold text-[#00ff88]">
                          <CountUp end={selectedProfile.engagement_rate} duration={2} decimals={1} suffix="%" />
                        </p>
                      </div>
                      <BarChart3 className="w-8 h-8 text-[#00ff88]" />
                    </div>
                  </motion.div>
                </div>
              </motion.div>

              {/* Growth Chart */}
              <motion.div 
                className="flex-1 p-6"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: 0.6 }}
              >
                <div className="bg-[#1a2332] rounded-xl p-6 border border-[#243447] h-full">
                  <div className="flex items-center justify-between mb-6">
                    <h3 className="text-lg font-bold text-white">Growth Trends (Last 30 Days)</h3>
                    <div className="flex items-center space-x-4 text-sm text-[#8b9bb3]">
                      <div className="flex items-center space-x-2">
                        <div className="w-3 h-3 bg-[#00d9ff] rounded-full"></div>
                        <span>Followers</span>
                      </div>
                      <div className="flex items-center space-x-2">
                        <div className="w-3 h-3 bg-[#00ff88] rounded-full"></div>
                        <span>Engagement</span>
                      </div>
                    </div>
                  </div>

                  <div className="h-80">
                    <ResponsiveContainer width="100%" height="100%">
                      <AreaChart data={selectedProfileGrowthData}>
                        <defs>
                          <linearGradient id="colorFollowers" x1="0" y1="0" x2="0" y2="1">
                            <stop offset="5%" stopColor="#00d9ff" stopOpacity={0.3}/>
                            <stop offset="95%" stopColor="#00d9ff" stopOpacity={0}/>
                          </linearGradient>
                          <linearGradient id="colorEngagement" x1="0" y1="0" x2="0" y2="1">
                            <stop offset="5%" stopColor="#00ff88" stopOpacity={0.3}/>
                            <stop offset="95%" stopColor="#00ff88" stopOpacity={0}/>
                          </linearGradient>
                        </defs>
                        <CartesianGrid strokeDasharray="3 3" stroke="#243447" />
                        <XAxis 
                          dataKey="date" 
                          stroke="#5a6b82"
                          tick={{ fontSize: 12 }}
                          tickFormatter={(value) => new Date(value).toLocaleDateString('en-US', { month: 'short', day: 'numeric' })}
                        />
                        <YAxis 
                          stroke="#5a6b82"
                          tick={{ fontSize: 12 }}
                        />
                        <Tooltip 
                          contentStyle={{ 
                            backgroundColor: '#243447', 
                            border: '1px solid #1a2332',
                            borderRadius: '12px',
                            color: '#ffffff'
                          }}
                          labelFormatter={(value) => new Date(value).toLocaleDateString()}
                          formatter={(value: number, name: string) => {
                            if (name === 'followers') return [`${value.toFixed(2)}M followers`, 'Followers'];
                            return [`${value.toFixed(1)}%`, 'Engagement Rate'];
                          }}
                        />
                        <Area 
                          type="monotone" 
                          dataKey="followers" 
                          stroke="#00d9ff" 
                          strokeWidth={3}
                          fill="url(#colorFollowers)"
                          name="Followers"
                        />
                        <Area 
                          type="monotone" 
                          dataKey="engagement" 
                          stroke="#00ff88" 
                          strokeWidth={3}
                          fill="url(#colorEngagement)"
                          name="Engagement"
                        />
                      </AreaChart>
                    </ResponsiveContainer>
                  </div>
                </div>
              </motion.div>

              {/* Last Updated */}
              <div className="p-6 border-t border-[#1a2332]">
                <div className="flex items-center space-x-2 text-[#8b9bb3] text-sm">
                  <Calendar className="w-4 h-4" />
                  <span>Last updated: {new Date(selectedProfile.last_updated).toLocaleString()}</span>
                </div>
              </div>
            </>
          ) : (
            <div className="flex-1 flex items-center justify-center">
              <div className="text-center">
                <div className="w-16 h-16 bg-[#243447] rounded-full flex items-center justify-center mx-auto mb-4">
                  <Users className="w-8 h-8 text-[#8b9bb3]" />
                </div>
                <h3 className="text-lg font-medium text-[#8b9bb3] mb-2">No Profile Selected</h3>
                <p className="text-[#5a6b82] text-sm">Select a profile from the list to view detailed analytics</p>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default SplitPanelDashboard;
